  `sid` int(11) NOT NULL AUTO_INCREMENT,
  `recipe_sid` int(11) NOT NULL,
  `step_number` int(11) NOT NULL,
  `step_introduction` text DEFAULT NULL,
  PRIMARY KEY (`sid`)
) ENGINE=InnoDB AUTO_INCREMENT=99 DEFAULT CHARSET=utf8;
