DROP TABLE IF EXISTS `solarterms_recipe`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `solarterms_recipe` (
  `sid` int(11) NOT NULL AUTO_INCREMENT,
  `date` date DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `step_img` varchar(255) DEFAULT NULL,
  `recipe_name` varchar(255) NOT NULL,
  `seasons_sid` int(11) NOT NULL,
  `solarterm_sid` int(11) NOT NULL,
  `vegetarian_sid` int(11) NOT NULL,
  `introduction` text NOT NULL,
  `quantity` int(11) NOT NULL,
  `time` int(11) NOT NULL,
  `Ingredients_sid` int(11) NOT NULL,
  `step_sid` int(11) NOT NULL,
  PRIMARY KEY (`sid`)
) ENGINE=InnoDB AUTO_INCREMENT=43 DEFAULT CHARSET=utf8;
