INSERT INTO `solarterms_recipe_ingredients` VALUES (1,1,'青花椰菜','1顆'),(2,1,'白花椰菜','半顆'),(3,1,'香蒜粒','適量'),(4,1,'雙色焗烤乳酪絲','適量'),(5,1,'海鹽','適量'),(6,1,'橄欖油','適量'),(7,1,'蘿勒葉','適量'),(8,2,'年糕','1包'),(9,2,'白菜','半份'),(10,2,'雪菜','適量'),(11,2,'豆干','適量'),(12,2,'洋蔥','1/4顆'),(13,2,'紅蘿蔔','1/8根'),(14,2,'杏鮑菇','4根'),(15,2,'鮑魚菇','2朵'),(16,2,'蛋液','2顆'),(17,2,'醬油','1大匙'),(18,2,'味霖','少許'),(19,2,'白胡椒','適量'),(20,2,'烏醋','適量'),(21,2,'香油','適量'),(22,3,'紅糖','45g'),(23,3,'烤焗花生碎','30g'),(24,3,'中筋麵粉','200g'),(25,3,'水','100g'),(26,3,'酵母','2g'),(27,4,'萵苣','適量'),(28,4,'香菇','6朵'),(29,4,'蒜頭','3瓣'),(30,4,'蠔油','1-2大匙'),(31,4,'鰹魚粉(內附的湯匙)','1小茶匙'),(32,4,'鹽（可不加）','適量'),(33,5,'油菜','1把'),(34,5,'薑','適量'),(35,5,'鹽','適量'),(36,6,'五香豆乾','8片'),(37,6,'蒜末','適量'),(38,6,'香菜','一小把'),(39,7,'紅蘿蔔','150克'),(40,7,'雞蛋','2顆'),(41,7,'蔥','適量'),(42,7,'油','2大匙(30ml)'),(43,7,'鹽巴','1/4小匙'),(44,8,'黑木耳QQ圓(黑糖口味)','1杯'),(45,8,'萵苣','1/2顆'),(46,8,'蛋白丁','1包'),(47,8,'乳酪條','1條'),(48,8,'泰式甜雞醬','適量'),(49,8,'七味粉','適量'),(50,9,'板豆腐／硬豆腐','200克'),(51,9,'紅蘿蔔丁','30克'),(52,9,'鴻禧菇','25克'),(53,9,'雞肉絲','40克'),(54,9,'毛豆仁','25克'),(55,9,'香菇粉','1小匙'),(56,9,'醬油露','1小匙'),(57,9,'醬油蠔油','1大匙'),(58,9,'胡椒','1小匙'),(59,9,'雞蛋','1顆'),(60,10,'蘋果（切塊）','2顆'),(61,10,'西芹（切塊）','100g'),(62,10,'葡萄乾','50g'),(63,10,'檸檬香蜂草（切碎）','1大匙'),(64,10,'核桃','50g'),(65,10,'沙拉醬（蛋黃醬）','50g'),(66,10,'原味優格','50g'),(67,10,'鹽','少許'),(68,10,'胡椒','少許'),(69,10,'美生菜','3-4片'),(70,11,'芒果','一顆（約手掌大）'),(71,11,'鮮奶油','300g'),(72,11,'椰奶','100g'),(73,11,'消化餅','適量'),(74,11,'椰粉','適量（裝飾用）'),(75,11,'檸檬皮','適量（裝飾用）'),(76,12,'千張豆皮','3張'),(77,12,'融化奶油','適量'),(78,12,'糖粉或赤藻糖醇','裝飾用'),(79,12,'布丁液','80ml'),(80,12,'雞蛋','1顆'),(81,12,'鮮奶油','80ml'),(82,12,'鮮奶','100ml'),(83,12,'糖粉或赤藻糖醇','1大匙'),(84,12,'柳橙皮或黃檸檬片-提味用','數片'),(85,13,'生豆皮','4片'),(86,13,'香菜','40g'),(87,13,'玉米粉','少許'),(88,13,'白胡椒塩','適量'),(89,14,'綠竹筍','2支'),(90,14,'毛豆','約半飯碗'),(91,14,'鴻喜菇','1包'),(92,14,'小支紅蘿蔔','1支'),(93,14,'白米','1杯'),(94,14,'糙米','半杯'),(95,14,'蠔油(或是香菇素蠔油)','1大匙'),(96,15,'黃豆芽菜','200g'),(97,15,'青蔥','1根(15g)'),(98,15,'紅蘿蔔','1/4條(60g)'),(99,15,'蒜頭','2顆(10g)'